plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
}


repositories {
    mavenCentral()
    flatDir {
        dirs("libs")
    }
}

dependencies {
    implementation("software.amazon.awssdk:bedrockruntime:2.31.18")
    implementation("software.amazon.awssdk:netty-nio-client:2.31.18")
    implementation("org.slf4j:slf4j-api:2.0.9")
    implementation("org.json:json:20230227")
    implementation("io.reactivex.rxjava3:rxjava:3.1.8")
    implementation("io.projectreactor:reactor-core:3.6.2")
    implementation("org.apache.logging.log4j:log4j-api:2.20.0")
    implementation("org.apache.logging.log4j:log4j-core:2.20.0")
    implementation("org.apache.logging.log4j:log4j-slf4j-impl:2.20.0")
    implementation("org.apache.logging.log4j:log4j-slf4j2-impl:2.20.0")
    implementation("org.apache.httpcomponents:httpclient:4.5.13")
    implementation("org.eclipse.jetty:jetty-server:11.0.18")
    implementation("org.eclipse.jetty:jetty-servlet:11.0.18")
    implementation("org.eclipse.jetty:jetty-webapp:11.0.18")
    implementation("org.eclipse.jetty.websocket:websocket-jetty-server:11.0.18")
    implementation("org.eclipse.jetty.websocket:websocket-jetty-client:11.0.18")
    implementation("org.eclipse.jetty:jetty-util:11.0.18")
    implementation("org.eclipse.jetty:jetty-http:11.0.18")
    implementation("org.eclipse.jetty:jetty-io:11.0.18")
    implementation("jakarta.servlet:jakarta.servlet-api:5.0.0")
    implementation("org.eclipse.jetty:jetty-security:11.0.18")
    implementation("org.eclipse.jetty:jetty-annotations:11.0.18")
    implementation("org.eclipse.jetty:jetty-plus:11.0.18")
    implementation("org.eclipse.jetty:jetty-xml:11.0.18")
    implementation("org.eclipse.jetty.websocket:websocket-servlet:11.0.18")
    implementation("org.eclipse.jetty.websocket:websocket-core-server:11.0.18")
    implementation("org.eclipse.jetty.websocket:websocket-core-client:11.0.18")

    implementation("com.fasterxml.jackson.core:jackson-databind:2.15.2")
    implementation("com.fasterxml.jackson.core:jackson-core:2.15.2")
    implementation("com.fasterxml.jackson.core:jackson-annotations:2.15.2")
    compileOnly("org.projectlombok:lombok:1.18.30") // Use the latest version
    annotationProcessor("org.projectlombok:lombok:1.18.30")

    testImplementation(libs.junit.jupiter)
    testImplementation("org.mockito:mockito-core:5.8.0")
    testImplementation("org.mockito:mockito-junit-jupiter:5.8.0")

    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    implementation(libs.guava)
}

java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

application {
    mainClass = "org.example.BedrockInitiateClient"
}

tasks.named<Test>("test") {
    useJUnitPlatform()
}

tasks.withType<JavaCompile> {
    options.annotationProcessorPath = configurations.annotationProcessor.get()
}

